import React from "react";
import App, { AppInitialProps, AppContext } from "next/app";
import { wrapper } from "../store/index";

class WrappedApp extends App {
    static getInitialProps = async ({ Component, ctx}) => {
        ctx.store.dispatch({ type: ""})
    }
}
